let tablinks=document.getElementsByClassName("tab__links"),tabcontents=document.getElementsByClassName("tab__contents");function opentab(t){for(tablink of tablinks)tablink.classList.remove("active-link");for(tabcontent of tabcontents)tabcontent.classList.remove("active-tab");event.currentTarget.classList.add("active-link"),document.getElementById(t).classList.add("active-tab")}let sidemenu=document.getElementById("sidemenu");function openmenu(){sidemenu.style.right="0"}function closemenu(){sidemenu.style.right="-200px"}